import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../actions/userActions";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { Link } from "react-router-dom";
import "./Login.css";
const Login = ({ history, location }) => {
  console.log("value of history", history);
  console.log("value of location", location);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [toggle, setToggle] = useState(false);
  const redirect = location.search ? location.search.split("=")[1] : "/";
  console.log("value of redirect is", redirect);
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { loading, error, userCred } = userLogin;
  const toggleVisibility = () => {
    // console.log('clicked')
    setToggle(!toggle);
    // if (password.type === 'password') {
    //   password.type = 'text'
    // } else {
    //   password.type = 'password'
    // }
  };
  const hideShow = () => {};

  useEffect(() => {
    if (userCred) {
      console.log(userCred);
      // console.log('there is userCred')
      // console.log(userCred)
      history.push(redirect);
    }
  }, [history, userCred, redirect]);
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(login(email, password));
    // console.log('form submitted')
  };
  return (
    <div className="container">
      <div className="layout">
        <h1>SIGN IN</h1>
        {error && <Message variant="danger " message={error} />}
        {loading ? (
          <Loader />
        ) : (
          <form onSubmit={submitHandler}>
            <input
              className="form-field"
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter Email"
              required
            />
            <div className="password-eye">
              <input
                className="form-field "
                id="pass"
                type={toggle ? "text" : "password"}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter Password"
                required
              />
              {toggle ? (
                <i
                  id="eye"
                  className="fas fa-eye"
                  onClick={toggleVisibility}
                ></i>
              ) : (
                <i
                  className="fas fa-eye-slash"
                  id="eye"
                  onClick={toggleVisibility}
                ></i>
              )}
            </div>
            <div className="remember-me">
              <input type="checkbox" id="check" />
              <label htmlFor="check">Remember me</label>
            </div>
            <button className="btn" type="submit">
              Login
            </button>
            <Link className="linked" to="/register">
              Admin Registration
            </Link>
          </form>
        )}

        {/* {} */}
      </div>
    </div>
  );
};

export default Login;
